import React, { Component } from "react";
import Select, { components } from "react-select";
import axios from "axios";
import styled from "styled-components";
import { Box, Flex, Image, Heading, Text } from "rebass";
import green_tick from "./Images/green_tick.png";

const Wrapper = styled.section`
  padding: -2em;
  background: papayawhip;
`;

const customStyles = {
  // option: (provided, state) => ({
  //   ...provided,
  //   borderBottom: "1px dotted blue",
  //   color: state.isSelected ? "white" : "black",
  //   backgroundColor: state.isSelected ? "blue" : "white",
  // }),
  control: (provided) => ({
    ...provided,
    marginTop: "15%",
  }),
};

const singleValue = ({
  cx,
  getStyles,
  selectProps,
  data,
  isDisabled,
  className,
  ...props
}) => {
  const { sortCode, accountName, accountNumber, accountType } = data.value;
  return (
    <div>
      <div>{`${sortCode} ${accountNumber} ${accountType} ${accountName}`}</div>
    </div>
  );
};

const selectedOption = (props) => {
  return props.isSelected ? (
    <div>
      <label>{props.label}</label>
      &nbsp; &nbsp;
      <Image src={green_tick} sx={{ width: ["3%"] }}></Image>
    </div>
  ) : (
    <components.Option {...props} />
  );
};

export default class CustomSelect extends Component {
  constructor(props) {
    super(props);
    this.state = {
      selectOptions: [],
      sortCode: "",
      accountNumber: "",
      accountType: "",
      accountName: "",
    };
  }

  async getOptions() {
    const res = await axios.get("http://localhost:5000/users");
    const data = res.data;

    const options = data.map((d) => ({
      value: d,
      label: `${d.sortCode} ${d.accountNumber} ${d.accountType} ${d.accountName}`,
    }));
    this.setState({ selectOptions: options });
  }

  handleChange(e) {
    this.setState({
      sortCode: e.value,
      sortCode: e.label,
    });
  }

  componentDidMount() {
    this.getOptions();
  }

  render() {
    console.log(this.state.selectOptions);
    return (
      <>
        <Wrapper>
          <div>
            <Flex flexWrap="wrap" mx={-5}>
              <Box
                p={5}
                width={[1, 1, 1 / 2]}
                fontSize={3}
                sx={{ px: 3, mx: "auto" }}
              >
                <Heading fontSize={40} color="blue" textAlign="center">
                  React Select Dropdown
                </Heading>
                <Select
                  components={{
                    Option: selectedOption,
                    SingleValue: singleValue,
                  }}
                  styles={customStyles}
                  options={this.state.selectOptions}
                  onChange={this.handleChange.bind(this)}
                />
              </Box>
            </Flex>
            <p>
              <Text fontSize={[2, 2, 2]} fontWeight="bold" color="blue">
                {this.state.sortCode}
              </Text>
            </p>
          </div>
        </Wrapper>
      </>
    );
  }
}
