import React from "react";

class RestController extends React.Component {
  constructor(props) {
    super(props);
    this.state = { user: [] };
  }

  componentDidMount() {
    fetch("https://jsonplaceholder.typicode.com/posts", {
      method: "POST",
      body: JSON.stringify({
        options: [
          {
            sortCode: "11-22-35",
            accountNumber: "123456791",
            accountType: "Savings Account",
            accountName: "Ajay Naphade",
          },
          {
            sortCode: "11-22-36",
            accountNumber: "123456792",
            accountType: "Savings Account",
            accountName: "Dilip Kulkarni",
          },
          {
            sortCode: "11-22-37",
            accountNumber: "123456793",
            accountType: "Savings Account",
            accountName: "Gauri Singh",
          },
        ],
      }),
      headers: {
        "Content-type": "application/json; charset=UTF-8",
      },
    })
      .then((response) => {
        return response.json();
      })
      .then((json) => {
        this.setState({
          user: json,
        });
      });
  }
  render() {
    return (
      <div>
        <p>
          <b>New Resource created in the server as shown below</b>
        </p>
        <p>sortCode : {this.state.user.sortCode}</p>
        <p>accountNumber : {this.state.user.accountNumber}</p>
        <p>accountType : {this.state.accountType}</p>
        <p>accountName : {this.state.user.accountName}</p>
      </div>
    );
  }
}

export default RestController;
