//I created a Fake REST API with JSON-Server, named as db.json
//db.json is going to act as our backened data store
import React, { Component } from "react";
import Select from "react-select";
import axios from "axios";

export default class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      selectOptions: [],
      id: "",
      sortCode: "",
      // accountNumber: "",
      // accountType: "",
      // accountName: "",
    };
  }

  async getOptions() {
    const res = await axios.get("http://localhost:5000/json");
    const data = res.data;

    const options = data.map((d) => ({
      value: d.id,
      label: d.sortCode,
      // label: d.accountNumber,
      // value: d.accountType,
      // value: d.accountName,
    }));

    const customStyles = {
      container: (base, state) => ({
        ...base,
        border: state.isFocused ? null : null,
        transition:
          "border-color 0.2s ease, box-shadow 0.2s ease, padding 0.2s ease",
        "&:hover": {
          boxShadow: "0 2px 4px 0 rgba(41, 56, 78, 0.1)"
        }
      }),
      control: (base, state) => ({
        ...base,
        background: "#bada55"
      }),
      valueContainer: (base, state) => ({
        ...base,
        background: "pink"
      })
    };  

    this.setState({ selectOptions: options });
  }

  handleChange(e) {
    this.setState({ id: e.value, sortCode: e.label });
  }

  componentDidMount() {
    this.getOptions();
  }


  render() {
    console.log(this.state.selectOptions);
    return (
      <div>
        <Select
          options={this.state.selectOptions}
          onChange={this.handleChange.bind(this)}
        />
        <p>
          <strong>{this.state.sortCode}</strong>
        </p>
        <p>
        <Select styles={customStyles} />
        </p>
      </div>
    );
  }
}
