const customSelect = () => {
  return (
    <>
      <Wrapper>
        <div>
          <Flex flexWrap="wrap" mx={-5}>
            <Box
              p={5}
              width={[1, 1, 1 / 2]}
              fontSize={3}
              sx={{ px: 3, mx: "auto" }}
            >
              <Heading fontSize={40} color="blue" textAlign="center">
                React Select Dropdown
              </Heading>
              <Select
                components={{
                  Option: selectedOption,
                  SingleValue: singleOption,
                }}
                styles={customStyles}
                options={this.state.selectOptions}
                onChange={this.handleChange.bind(this)}
              />
            </Box>
          </Flex>
          <p>
            <Text fontSize={[2, 2, 2]} fontWeight="bold" color="blue">
              {this.state.sortCode}
            </Text>
          </p>
        </div>
      </Wrapper>
    </>
  );
};

export default customSelect;
