//I created a Fake REST API with JSON-Server, named as db.json
//db.json is going to act as our backened data store
import React, { Component } from "react";
import Select from "react-select";
import axios from "axios";
class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      selectOptions: [],
      sortCode: "",
      accountNumber: "",
      accountType: "",
      accountName: "",
    };
  }
  async getOptions() {
    const res = await axios.get("http://localhost:5000/users");
    const data = res.data;

    const options = data.map((d) => ([{
      value: d.sortCode,
      label: d.accountNumber,
      label: d.accountType,
      label: d.accountName,
    }]));

    this.setState({ selectOptions: options });
  }
  componentDidMount() {
    this.getOptions();
  }


  render() {
    console.log(this.state.selectOptions)
    return (
      <div>
        <Select options={this.state.selectOptions} />
      </div>
    )
  }
}
export default App;
