// I created a Fake REST API with JSON-Server, named as db.json
// db.json is going to act as our backened data store
import React, { Component } from "react";
import Select from "react-select";
import axios from "axios";
import Tick from "./Tick.png";

const tick = (e) => {
	return ( 
        <div className="shoe-container">
            <img src={Tick} alt=""/>
        </div>
    );
  }
  // const dot = (color = '#ccc') => ({
  //   alignItems: 'center',
  //   display: 'flex',
  
  //   ':before': {
  //     backgroundColor: color,
  //     borderRadius: 10,
  //     content: '" "',
  //     display: 'block',
  //     marginRight: 8,
  //     height: 10,
  //     width: 10,
  //   },
  // });

const colourStyles = {
  option: (styles, { data, isDisabled, isFocused, isSelected }) => {
    // const color = chroma(data.color);
    console.log({ data, isDisabled, isFocused, isSelected });
    return {
      ...styles,
      backgroundColor: isFocused ? "sky-blue" : null,
      color: "#333333"
    };
  }
};

export default class CustomSelect extends Component {
  constructor(props) {
    super(props);
    this.state = {
      selectOptions: [],
      id: "",
      sortCode: "",
      accountNumber: "",
      accountType: "",
      accountName: "",
    };
  }

  async getOptions() {
    const res = await axios.get("http://localhost:5000/users");
    const data = res.data;

    const options = data.map((d) => ({
      value: d.id,
      label: `${d.sortCode} ${d.accountNumber} ${d.accountType} ${d.accountName}`,
    }));

    this.setState({ selectOptions: options });
  }

  handleChange(e) {
    this.setState({
      id: e.value,
      sortCode: e.label,
      // accountNumber: e.label,
      // accountType: e.label,
      // accountName: e.label,
    });
  }

  componentDidMount() {
    this.getOptions();
  }

  render() {
    console.log(this.state.selectOptions);
    return (
      <div >
        <Select
          options={this.state.selectOptions}
          onChange={this.handleChange.bind(this)}
          styles={colourStyles}
        />
        <p>
          <strong>{this.state.sortCode}</strong>
        </p>
      </div>
    );
  }
}
