// I created a Fake REST API with JSON-Server, named as db.json
// db.json is going to act as our backened data store

import React, { Component } from "react";
import styled from 'styled-components'
// import React, { Component } lets you do class Happy extends Component instead of class Menu extends React.Component.
// It's less typing and duplication of the React namespace, which is generally a desired modern coding convention.
import Select from "react-select";
import axios from "axios";
import { Box, Flex } from "rebass";

// const tick = (e) => {
// 	return (
//         <div className="shoe-container">
//         <img src={process.env.PUBLIC_URL + '/green-tick.png'} />;
//         </div>
//     );
//   }
// const dot = (color = '#ccc') => ({
//   alignItems: 'center',
//   display: 'flex',

//   ':before': {
//     backgroundColor: color,
//     borderRadius: 10,
//     content: '" "',
//     display: 'block',
//     marginRight: 8,
//     height: 10,
//     width: 10,
//   },
// });

const Wrapper = styled.section`
  padding: -2em;
  background: papayawhip;
`;

const customStyles = {
  option: (provided, state) => ({
    ...provided,
    borderBottom: "2px dotted blue",
    color: state.isSelected ? "white" : "black",
    backgroundColor: state.isSelected ? "blue" : "white",
    "&:after": {
      fontFamily: "FontAwesome",
      content: '"\f00c"',
    },
    // backgroundImage: state.isSelected? '/green-tick.png' : ''
  }),
  control: (provided) => ({
    ...provided,
    marginTop: "10%",
  }),
  // singleValue: (provided, state) => ({
  //   ...provided,
  //   '&:after':{
  //     fontFamily: "FontAwesome",
  //     back:'"/green-tick.png"'
  //   }
  // })
};

export default class CustomSelect extends Component {
  constructor(props) {
    super(props);
    this.state = {
      selectOptions: [],
      id: "",
      sortCode: "",
      accountNumber: "",
      accountType: "",
      accountName: "",
      // selectedItem: ""
    };
  }

  async getOptions() {
    const res = await axios.get("http://localhost:5000/users");
    const data = res.data;

    const options = data.map((d) => ({
      value: d.id,
      label: `${d.sortCode} ${d.accountNumber} ${d.accountType} ${d.accountName}`,
    }));
    this.setState({ selectOptions: options });
  }

  handleChange(e) {
    this.setState({
      id: e.value,
      sortCode: e.label,
      // accountNumber: e.label,
      // accountType: e.label,
      // accountName: e.label,
    });
  }

  // onRenderOption = (options) => {
  //   return (
  //     <>
  //       <div className={options.isChecked ? 'ms-Icon ms-Icon--CheckMark' : ''}>
  //         {option.text}
  //       </div>
  //     </>
  //   );
  // };

  componentDidMount() {
    this.getOptions();
  }

  render() {
    console.log(this.state.selectOptions);
    return (
      <Wrapper>
        <div>
          <Flex flexWrap="wrap" mx={-2}>
            <Box
              p={5}
              width={[1, 1, 1 / 2]}
              fontSize={3}
              sx={{ px: 3, mx: "auto" }}
            >
              <Select
                // multiselect
                styles={customStyles}
                options={this.state.selectOptions}
                onChange={this.handleChange.bind(this)}
                // onRenderOption={this.onRenderOption}
              />
            </Box>
          </Flex>
          <p>
            <strong>{this.state.sortCode}</strong>
          </p>
        </div>
      </Wrapper>
    );
  }
}
