// I created a Fake REST API with JSON-Server, named as db.json
// db.json is going to act as our backened data store

import React, { Component } from "react";
import styled from "styled-components";
// import React, { Component } lets you do class Happy extends Component instead of class Menu extends React.Component.
// It's less typing and duplication of the React namespace, which is generally a desired modern coding convention.
import Select from "react-select";
import axios from "axios";
import { Box, Flex } from "rebass";
import green_tick from "./Images/green_tick.png";

const Wrapper = styled.section`
  padding: -2em;
  background: papayawhip;
`;

const customStyles = {
  option: (provided, state) => ({
    ...provided,
    borderBottom: "2px dotted blue",
    color: state.isSelected ? "white" : "black",
    backgroundColor: state.isSelected ? "blue" : "white",
    textAlign: state.isSelected ? "vertical" : "",
    // "&:after": {
    //   fontFamily: "FontAwesome",
    //   content: '"\f00c"',
    // },
    // backgroundImage: state.isSelected? '/green-tick.png' : ''
  }),
  control: (provided) => ({
    ...provided,
    marginTop: "10%",
  }),
  // singleValue: (provided, state) => ({
  //   ...provided,
  //   '&:after':{
  //     fontFamily: "FontAwesome",
  //     back:'"/green-tick.png"'
  //   }
  // })
};

// const CustomOption = (props) => {
//   console.log(props.label);
//   return props.isSelected ? (
//     <div>
//       <label>{props.label}</label>
//       <Image
//         src={green_tick}
//         sx={{
//           width: ["2%"],
//         }}
//       ></Image>
//     </div>
//   ) : (
//     <components.Option {...props} />
//   );
// };

export default class CustomSelect extends Component {
  constructor(props) {
    super(props);
    this.state = {
      selectOptions: [],
      id: "",
      sortCode: "",
      accountNumber: "",
      accountType: "",
      accountName: "",
      // selectedItem: ""
    };
  }

  async getOptions() {
    const res = await axios.get("http://localhost:5000/users");
    const data = res.data;

    const options = data.map((d) => ({
      value: d.id,
      label: `${d.sortCode} ${d.accountNumber} ${d.accountType} ${d.accountName}`,
    }));
    this.setState({ selectOptions: options });
  }

  handleChange(e) {
    this.setState({
      id: e.value,
      sortCode: e.label,
    });
  }

  onRenderOption = (options) => {
    console.log(options.label);
    return options.isSelected ? (
      // <>
      <div>
        <label>{options.label}</label>
        <Image src={green_tick}
          sx={{
            width: ["2%"],
          }}
        ></Image>
      </div>
    ) : (
      //  </>
      <options {...props} />
    );
  };

  // const CustomOption = (props) => {
  //   console.log(props.label);
  //   return props.isSelected ? (
  //     <div>
  //       <label>{props.label}</label>
  //       <Image
  //         src={green_tick}
  //         sx={{
  //           width: ["2%"],
  //         }}
  //       ></Image>
  //     </div>
  //   ) : (
  //     <components.Option {...props} />
  //   );
  // };

  componentDidMount() {
    this.getOptions();
  }

  render() {
    console.log(this.state.selectOptions);
    return (
      <Wrapper>
        <div>
          <Flex flexWrap="wrap" mx={-4}>
            <Box
              p={5}
              width={[1, 1, 1 / 2]}
              fontSize={3}
              sx={{ px: 3, mx: "auto" }}
            >
              <Select
                // multiselect
                styles={customStyles}
                options={this.state.selectOptions}
                onChange={this.handleChange.bind(this)}
                onRenderOption={this.state.onRenderOption}
              />
            </Box>
          </Flex>
          <p>
            <strong>{this.state.sortCode}</strong>
          </p>
        </div>
      </Wrapper>
    );
  }
}
